CREATE TABLE `party` (`id` BINARY(16) NOT NULL,`identity` VARCHAR(20) NOT NULL,`type` VARCHAR(20) NOT NULL,`name` VARCHAR(50) NOT NULL,`email` VARCHAR(50) NULL,`enabled` BIT(1) NOT NULL,`creation_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,`modification_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,PRIMARY KEY (`id`),UNIQUE INDEX `identity` (`identity`),INDEX `type` (`type`),INDEX `name` (`name`),INDEX `enabled` (`enabled`),INDEX `creation_date` (`creation_date`),INDEX `modification_date` (`modification_date`));
CREATE TABLE `party_rel` (`id` INT NOT NULL AUTO_INCREMENT, `parent` BINARY(16) NOT NULL, `children` BINARY(16) NOT NULL, PRIMARY KEY (`id`), INDEX `parent` (`parent`), INDEX `children` (`children`), CONSTRAINT `FK__party_rel_parent` FOREIGN KEY (`parent`) REFERENCES `party` (`id`), CONSTRAINT `FK__party_rel_children` FOREIGN KEY (`children`) REFERENCES `party` (`id`));